mapper.select:
select

from platform2b_product_rel
where product_id = #{productId} and platform_id = #{platformId} order by id limit 1;

select count(1)
from platform2b_product_rel pr
where pr.allot_State = #{allotState}
and pr.product_id = #{productId}
and pr.platform_id = #{platformId}

select DISTINCT product_id from platform2b_product_rel pr where is_deleted = 0
and product_id in
#{id}

