		and att1  like CONCAT(#{schAtt1_,jdbcType=VARCHAR},'%')


		and att1 between #{att1Start,jdbcType=VARCHAR} and #{att1End,jdbcType=VARCHAR}


	   and area_long_code  = #{areaLongCode,jdbcType=VARCHAR} 

		and area_long_code  like CONCAT(CONCAT('%',#{sch_AreaLongCode_,jdbcType=VARCHAR}),'%')


		and area_long_code  like CONCAT('%',#{sch_AreaLongCode,jdbcType=VARCHAR})


		and area_long_code  like CONCAT(#{schAreaLongCode_,jdbcType=VARCHAR},'%')


		and area_long_code between #{areaLongCodeStart,jdbcType=VARCHAR} and #{areaLongCodeEnd,jdbcType=VARCHAR}


		order by ${order}

			${orderNum}

select count(*)
from base_house
where 1=1
 and creator  = #{creator,jdbcType=VARCHAR} 

	and gmt_create  = #{gmtCreate,jdbcType=TIMESTAMP}


	and gmt_create between #{gmtCreateStart,jdbcType=TIMESTAMP} and #{gmtCreateEnd,jdbcType=TIMESTAMP}

			and is_deleted  = #{isDeleted,jdbcType=INTEGER}

