mapper.update:
update design_plan_check
plan_id = #{planId,jdbcType=INTEGER}, user_id = #{userId,jdbcType=VARCHAR}, sys_code = #{sysCode,jdbcType=VARCHAR}, creator = #{creator,jdbcType=VARCHAR}, gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}, modifier = #{modifier,jdbcType=VARCHAR}, gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}, is_deleted = #{isDeleted,jdbcType=INTEGER}, att1 = #{att1,jdbcType=VARCHAR}, att2 = #{att2,jdbcType=VARCHAR}, numa1 = #{numa1,jdbcType=INTEGER}, numa2 = #{numa2,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

