SELECT COUNT(*) FROM design_plan_check
WHERE 1=1 
  and plan_id = #{planId,jdbcType=INTEGER} 
  and user_id = #{userId,jdbcType=INTEGER} 
  and sys_code = #{sysCode,jdbcType=VARCHAR} 
  and creator = #{creator,jdbcType=VARCHAR} 
  and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
  and modifier = #{modifier,jdbcType=VARCHAR} 
  and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
  and is_deleted = #{isDeleted,jdbcType=INTEGER} 
  and att1 = #{att1,jdbcType=VARCHAR} 
  and att2 = #{att2,jdbcType=VARCHAR} 
  and numa1 = #{numa1,jdbcType=INTEGER} 
  and numa2 = #{numa2,jdbcType=INTEGER} 
  and remark = #{remark,jdbcType=VARCHAR}

select DISTINCT dp.id as plan_id,rrp.pic_path as cover_path,dp.plan_name,dp.is_release, dp.gmt_modified,dp.gmt_create,su.user_name as 'creator' ,sc.space_areas , sc.space_function_id, dp.release_time,dp.living_id,dp.house_id,dp.remark,dp.plan_source,dp.is_default_decorate from design_plan as dp LEFT JOIN design_plan_brand as dpb on(dp.id = dpb.plan_id ) LEFT JOIN base_brand as bb on(dpb.brand_id = bb.id) LEFT JOIN design_plan_check as dpc on(dp.id = dpc.plan_id) LEFT JOIN design_templet as dt on(dp.design_template_id = dt.id ) LEFT JOIN space_common as sc on(sc.id = dt.space_common_id) LEFT JOIN res_render_pic as rrp on(dp.cover_pic_id = rrp.id ) LEFT JOIN sys_user as su on (dp.user_id = su.id) and su.is_deleted = 0 where 1 = 1 and dp.is_deleted = 0 and (dp.is_release = 1 or dp.is_release = 3 ) and dpc.user_id = #{userId,jdbcType=INTEGER}
and sc.space_function_id = #{spaceFunctionId,jdbcType=INTEGER}
and dp.plan_name like CONCAT(#{planName,jdbcType=VARCHAR},'%')
and bb.brand_name = #{brandName,jdbcType=VARCHAR}
and dp.plan_number like CONCAT(CONCAT('%',#{planNumber,jdbcType=VARCHAR}),'%')
order by dp.release_time desc LIMIT #{start}, #{limit}

select count(DISTINCT dp.id) from design_plan as dp LEFT JOIN design_plan_brand as dpb on(dp.id = dpb.plan_id ) LEFT JOIN base_brand as bb on(dpb.brand_id = bb.id) LEFT JOIN design_plan_check as dpc on(dp.id = dpc.plan_id) LEFT JOIN design_templet as dt on(dp.design_template_id = dt.id ) LEFT JOIN space_common as sc on(sc.id = dt.space_common_id) LEFT JOIN res_render_pic as rrp on(dp.cover_pic_id = rrp.id ) LEFT JOIN sys_user as su on (dp.user_id = su.id) and su.is_deleted = 0 where 1 = 1 and dp.is_deleted = 0 and (dp.is_release = 1 or dp.is_release = 3 ) and dpc.user_id = #{userId,jdbcType=INTEGER}
and sc.space_function_id = #{spaceFunctionId,jdbcType=INTEGER}
and dp.plan_name like CONCAT(#{planName,jdbcType=VARCHAR},'%')
and bb.brand_name = #{brandName,jdbcType=VARCHAR}
and dp.plan_number like CONCAT(CONCAT('%',#{planNumber,jdbcType=VARCHAR}),'%')
