mapper.select:
SELECT DISTINCT c.id AS planRecommendedId FROM design_plan_recommend_favorite_ref a LEFT JOIN sys_business_favorite b ON b.bid = a.fid LEFT JOIN design_plan_recommended c ON c.id = a.recommendId LEFT JOIN space_common d ON d.id = c.space_common_id WHERE b.userId = #{userId} AND a.status = 1 AND a.design_plan_type = 0
and c.company_id IN
#{item}
AND c.plan_name like CONCAT(CONCAT('%',#{searchKeyword, jdbcType=VARCHAR}),'%')
AND d.space_function_id = #{houseType}
AND c.design_recommended_style_id = #{designStyleId}
AND c.is_deleted = 0 order by a.utime DESC

SELECT count(1) FROM (SELECT dprfr.recommendId, dprfr.full_house_design_plan_id FROM design_plan_recommend_favorite_ref AS dprfr LEFT JOIN sys_business_favorite AS sbf ON ( dprfr.fid = sbf.bid ) WHERE sbf.userId = #{userId} AND dprfr.STATUS = 1 ) AS t1 LEFT JOIN ( SELECT * FROM design_plan_recommended WHERE is_deleted = 0 AND is_release = 1
and company_id = #{companyId}
) AS t2 ON ( t1.recommendId = t2.id ) LEFT JOIN design_plan_2c_platform t3 on t3.plan_id = t2.id and t3.platform_id = #{platformId} and t3.design_plan_type != 3 LEFT JOIN full_house_design_plan t4 on t4.is_deleted = 0 and t4.id = t1.full_house_design_plan_id
and t4.company_id = #{companyId}
LEFT JOIN design_plan_2c_platform t5 on t5.plan_id = t4.id and t5.platform_id = #{platformId} and t5.design_plan_type = 3 WHERE 1 = 1
and (t3.allot_state = 1 or t5.allot_state = 1)
