mapper.update:
update user_access
access_id = #{accessId,jdbcType=INTEGER}, be_accessed_id = #{beAccessedId,jdbcType=INTEGER}, sys_code = #{sysCode,jdbcType=VARCHAR}, creator = #{creator,jdbcType=VARCHAR}, gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}, modifier = #{modifier,jdbcType=VARCHAR}, gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}, is_deleted = #{isDeleted,jdbcType=INTEGER}, att1 = #{att1,jdbcType=VARCHAR}, att2 = #{att2,jdbcType=VARCHAR}, numa1 = #{numa1,jdbcType=INTEGER}, numa2 = #{numa2,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:
		select

		from user_access
        where 1=1
		  and access_id = #{accessId,jdbcType=INTEGER} 
		  and be_accessed_id = #{beAccessedId,jdbcType=INTEGER} 
		  and sys_code = #{sysCode,jdbcType=VARCHAR} 
		  and creator = #{creator,jdbcType=VARCHAR} 
		  and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
		  and modifier = #{modifier,jdbcType=VARCHAR} 
		  and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
		  and is_deleted = #{isDeleted,jdbcType=INTEGER} 
		  and att1 = #{att1,jdbcType=VARCHAR} 
		  and att2 = #{att2,jdbcType=VARCHAR} 
		  and numa1 = #{numa1,jdbcType=INTEGER} 
		  and numa2 = #{numa2,jdbcType=INTEGER} 
		  and remark = #{remark,jdbcType=VARCHAR} 

			order by ${order}

				${orderNum}



		   order by ${orders}

	SELECT count(*)
    FROM user_access a 
    LEFT JOIN sys_user s on a.access_id = s.id
    LEFT JOIN sys_user su on a.be_accessed_id = su.id
	WHERE 1=1	
				and s.user_name like CONCAT(CONCAT('%',#{accessName,jdbcType=VARCHAR}),'%')  
			and su.user_name like CONCAT(CONCAT('%',#{beAccessedName,jdbcType=VARCHAR}),'%')  
				and access_id  = #{accessId,jdbcType=INTEGER} 
				and be_accessed_id  = #{beAccessedId,jdbcType=INTEGER} 

		and a.gmt_create  = #{gmtCreate,jdbcType=TIMESTAMP}


		and a.gmt_create between #{gmtCreateStart,jdbcType=TIMESTAMP} and #{gmtCreateEnd,jdbcType=TIMESTAMP}

	 and modifier  = #{modifier,jdbcType=VARCHAR} 

		and a.modifier  like CONCAT(CONCAT('%',#{sch_Modifier_,jdbcType=VARCHAR}),'%')


		and a.modifier  like CONCAT('%',#{sch_Modifier,jdbcType=VARCHAR})


		and a.modifier  like CONCAT(#{schModifier_,jdbcType=VARCHAR},'%')


		and a.modifier between #{modifierStart,jdbcType=VARCHAR} and #{modifierEnd,jdbcType=VARCHAR}


		and a.gmt_modified  = #{gmtModified,jdbcType=TIMESTAMP}


		and a.gmt_modified between #{gmtModifiedStart,jdbcType=TIMESTAMP} and #{gmtModifiedEnd,jdbcType=TIMESTAMP}

				and a.is_deleted  = #{isDeleted,jdbcType=INTEGER}

	SELECT
	a.access_id ,a.be_accessed_id,a.gmt_create
    FROM user_access a 
    LEFT JOIN sys_user s on a.access_id = s.id
     LEFT JOIN sys_user su on a.be_accessed_id = su.id
	WHERE 1=1	
				and s.user_name like CONCAT(CONCAT('%',#{accessName,jdbcType=VARCHAR}),'%')  
			and su.user_name like CONCAT(CONCAT('%',#{beAccessedName,jdbcType=VARCHAR}),'%')  
				and access_id  = #{accessId,jdbcType=INTEGER} 
				and be_accessed_id  = #{beAccessedId,jdbcType=INTEGER} 

		and a.gmt_create  = #{gmtCreate,jdbcType=TIMESTAMP}


		and a.gmt_create between #{gmtCreateStart,jdbcType=TIMESTAMP} and #{gmtCreateEnd,jdbcType=TIMESTAMP}

	 and a.modifier  = #{modifier,jdbcType=VARCHAR} 

		and a.modifier  like CONCAT(CONCAT('%',#{sch_Modifier_,jdbcType=VARCHAR}),'%')


		and a.modifier  like CONCAT('%',#{sch_Modifier,jdbcType=VARCHAR})


		and a.modifier  like CONCAT(#{schModifier_,jdbcType=VARCHAR},'%')


		and a.modifier between #{modifierStart,jdbcType=VARCHAR} and #{modifierEnd,jdbcType=VARCHAR}


		and a.gmt_modified  = #{gmtModified,jdbcType=TIMESTAMP}


		and a.gmt_modified between #{gmtModifiedStart,jdbcType=TIMESTAMP} and #{gmtModifiedEnd,jdbcType=TIMESTAMP}

				and a.is_deleted  = #{isDeleted,jdbcType=INTEGER} 
	 and a.att1  = #{att1,jdbcType=VARCHAR} 

		and a.att1  like CONCAT(CONCAT('%',#{sch_Att1_,jdbcType=VARCHAR}),'%')


		and a.att1  like CONCAT('%',#{sch_Att1,jdbcType=VARCHAR})


		and a.att1  like CONCAT(#{schAtt1_,jdbcType=VARCHAR},'%')


		and a.att1 between #{att1Start,jdbcType=VARCHAR} and #{att1End,jdbcType=VARCHAR}

	 and att2  = #{att2,jdbcType=VARCHAR} 

		and a.att2  like CONCAT(CONCAT('%',#{sch_Att2_,jdbcType=VARCHAR}),'%')


		and a.att2  like CONCAT('%',#{sch_Att2,jdbcType=VARCHAR})


		and a.att2  like CONCAT(#{schAtt2_,jdbcType=VARCHAR},'%')


		and a.att2 between #{att2Start,jdbcType=VARCHAR} and #{att2End,jdbcType=VARCHAR}



		order by ${order} ${orderNum}

	  order by #{orders}

	 limit #{start}, #{limit}
