mapper.select:
select

from auto_render_task
where 1=1
 and plan_id = #{autoRenderTask.planId,jdbcType=INTEGER} 
 and template_id = #{autoRenderTask.templateId,jdbcType=INTEGER} 
	 and is_deleted =  0
 	order by priority desc
	LIMIT 0, #{autoRenderTask.maxSize}

mapper.update:
update auto_render_task_state
render_pic = #{renderPic,jdbcType=INTEGER}, render_720 = #{render720,jdbcType=INTEGER}, render_n720 = #{renderN720,jdbcType=INTEGER}, render_video = #{renderVideo,jdbcType=INTEGER}, state = #{state,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR}, fail_type = #{failType,jdbcType=INTEGER}, fail_reason = #{failReason,jdbcType=VARCHAR}, host_name = #{hostName,jdbcType=VARCHAR}, business_id = #{businessId,jdbcType=INTEGER}, creator = #{creator,jdbcType=VARCHAR}, gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}, modifier = #{modifier,jdbcType=VARCHAR}, gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}, design_plan_id = #{designPlanId,jdbcType=INTEGER}, task_id = #{taskId,jdbcType=INTEGER},
where 1 = 1 AND is_deleted = 0 AND plan_id = #{planId,jdbcType=INTEGER} AND template_id = #{templateId,jdbcType=VARCHAR}

update auto_render_task_state
render_pic = #{renderPic,jdbcType=INTEGER}, render_720 = #{render720,jdbcType=INTEGER}, render_n720 = #{renderN720,jdbcType=INTEGER}, render_video = #{renderVideo,jdbcType=INTEGER}, state = #{state,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR}, fail_type = #{failType,jdbcType=INTEGER}, fail_reason = #{failReason,jdbcType=VARCHAR}, host_name = #{hostName,jdbcType=VARCHAR}, business_id = #{businessId,jdbcType=INTEGER}, creator = #{creator,jdbcType=VARCHAR}, gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}, modifier = #{modifier,jdbcType=VARCHAR}, gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}, design_plan_id = #{designPlanId,jdbcType=INTEGER}, task_id = #{taskId,jdbcType=INTEGER},
where 1 = 1 and is_deleted = 0 and plan_id = #{planId,jdbcType=INTEGER}

update auto_render_task_state
is_valid = #{isValid,jdbcType=INTEGER}, is_deleted = #{isDeleted,jdbcType=INTEGER}, business_id = #{businessId,jdbcType=INTEGER}, render_pic = #{renderPic,jdbcType=INTEGER}, render_720 = #{render720,jdbcType=INTEGER}, render_n720 = #{renderN720,jdbcType=INTEGER}, render_video = #{renderVideo,jdbcType=INTEGER}, state = #{state,jdbcType=INTEGER}, fail_type = #{failType,jdbcType=INTEGER}, fail_reason = #{failReason,jdbcType=VARCHAR}, design_code = #{designCode,jdbcType=VARCHAR}, template_code = #{templateCode,jdbcType=VARCHAR}, new_design_code = #{newDesignCode,jdbcType=VARCHAR}, design_name = #{designName,jdbcType=VARCHAR}, task_source = #{taskSource,jdbcType=INTEGER}, host_ip = #{hostIp,jdbcType=VARCHAR}, render_time_consuming = #{renderTimeConsuming,jdbcType=VARCHAR}, platform_id = #{platformId,jdbcType=INTEGER},
where task_id = #{taskId,jdbcType=INTEGER}

mapper.select:
select
	arts.*,dp.plan_name as planName
from 
	auto_render_task arts
	LEFT JOIN design_plan dp ON dp.id = arts.`plan_id` 
where 1=1
	and arts.is_valid = 0
	  and arts.is_deleted = #{isDeleted,jdbcType=INTEGER} 
	  and arts.task_type = #{taskType,jdbcType=INTEGER} 
	  and arts.operation_user_id = #{operationUserId,jdbcType=INTEGER} 
 	  and arts.plan_id = #{planId,jdbcType=INTEGER} 
	  and arts.template_id = #{templateId,jdbcType=INTEGER} 
 	order by arts.gmt_create desc
	 LIMIT #{start}, #{limit}

select
	arts.*,dp.plan_name as planName,art.operation_source
from 
	auto_render_task_state arts
    LEFT JOIN auto_render_task art on arts.task_id = art.id
	LEFT JOIN design_plan dp ON dp.id = arts.`plan_id` 
where 1=1
	and arts.is_valid = 0
	  and arts.is_deleted = #{isDeleted,jdbcType=INTEGER} 
	  and arts.task_type = #{taskType,jdbcType=INTEGER} 
	  and arts.operation_user_id = #{operationUserId,jdbcType=INTEGER} 
	  and arts.plan_id = #{planId,jdbcType=INTEGER} 
	  and arts.template_id = #{templateId,jdbcType=INTEGER} 

	and arts.state != 0
 	order by arts.gmt_create desc
	 LIMIT #{start}, #{limit}

select
	id,plan_id,template_id,render_pic,render_720,render_n720,render_video,
	is_deleted,state,remark,fail_type,fail_reason,host_name,business_id,design_plan_id,task_id,order_number
	,operation_user_id,task_type,render_types_str
from 
	auto_render_task_state
where 1=1
	 and is_deleted = #{isDeleted,jdbcType=INTEGER} 
	 and task_type = #{taskType,jdbcType=INTEGER} 
	 and plan_id = #{planId,jdbcType=INTEGER} 
	 and render_types_str = #{renderTypesStr,jdbcType=INTEGER} 
	 and operation_user_id = #{operationUserId,jdbcType=INTEGER} 
	 and task_id = #{taskId,jdbcType=INTEGER}

select
	count(arts.id) as budget
from 
	auto_render_task_state arts
    LEFT JOIN auto_render_task art on arts.task_id = art.id
	LEFT JOIN design_plan dp ON dp.id = arts.`plan_id` 
where 1=1
	and arts.is_deleted = 0
	and arts.is_valid = 0
	  and arts.task_type = #{taskType,jdbcType=INTEGER} 
	  and arts.operation_user_id = #{operationUserId,jdbcType=INTEGER} 
	  and arts.plan_id = #{planId,jdbcType=INTEGER} 
	  and arts.template_id = #{templateId,jdbcType=INTEGER} 
	  and arts.state = #{state,jdbcType=INTEGER}
