mapper.update:
update sys_user_message
title = #{title,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, message_type = #{messageType,jdbcType=INTEGER}, task_id = #{taskId,jdbcType=INTEGER}, user_id = #{userId,jdbcType=INTEGER}, is_read = #{isRead,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER}, failing_reason = #{failingReason,jdbcType=VARCHAR}, sys_code = #{sysCode,jdbcType=VARCHAR}, creator = #{creator,jdbcType=VARCHAR}, gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}, modifier = #{modifier,jdbcType=VARCHAR}, gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}, is_deleted = #{isDeleted,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR}, platform_id = #{platformId,jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}

mapper.select:
		select
		 from sys_user_message
        where 1=1
		  and title = #{title,jdbcType=VARCHAR} 
		  and content = #{content,jdbcType=VARCHAR} 
		  and message_type = #{messageType,jdbcType=INTEGER} 
		  and task_id = #{taskId,jdbcType=INTEGER} 
		  and user_id = #{userId,jdbcType=INTEGER} 
		  and is_read = #{isRead,jdbcType=INTEGER} 
		  and status = #{status,jdbcType=INTEGER} 
		  and failing_reason = #{failingReason,jdbcType=VARCHAR} 
		  and sys_code = #{sysCode,jdbcType=VARCHAR} 
		  and creator = #{creator,jdbcType=VARCHAR} 
		  and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
		  and modifier = #{modifier,jdbcType=VARCHAR} 
		  and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
		  and is_deleted = #{isDeleted,jdbcType=INTEGER} 
		  and remark = #{remark,jdbcType=VARCHAR} 
		  and platform_id = #{platformId,jdbcType=INTEGER} 

			and platform_id in
			(select id from base_platform where platform_bussiness_type = #{platformBussinessType,jdbcType=VARCHAR})


			order by ${order}

				${orderNum}



		   order by ${orders}

		 LIMIT #{start}, #{limit}

mapper.update:
UPDATE sys_user_message SET is_deleted = 1 WHERE user_id = #{userId}

	and platform_id in
	(select id from base_platform where platform_bussiness_type = #{platformBussinessType,jdbcType=VARCHAR})

