SELECT t3.id AS companyId FROM sys_user AS t1 LEFT OUTER JOIN base_company AS t2 ON t1.business_administration_id = t2.id LEFT OUTER JOIN base_company AS t3 ON t2.pid = t3.id WHERE 1 = 1
AND t1.mobile = #{mobile}
AND t2.pid IS NOT NULL AND t1.user_type = '3' AND t1.is_deleted = '0' AND t2.is_deleted = '0' AND t3.is_deleted = '0' ORDER BY t3.id DESC

SELECT t2.id AS companyId FROM sys_user AS t1 LEFT OUTER JOIN base_company AS t2 ON t1.business_administration_id = t2.id WHERE 1 = 1
AND t1.mobile = #{mobile}
AND t2.id IS NOT NULL AND t1.user_type = '2' AND t1.is_deleted = '0' AND t2.is_deleted = '0' ORDER BY t2.id DESC
