select bwt.id as id, bwt.template_name as templateName, bwt.template_length as templateLength, bwt.template_width as templateWidth, rp.pic_path as templatePicUrl, rp.small_pic_info as templateSmallPicInfo, rm.model_path as templateFileUrl from base_waterjet_template bwt left join res_pic rp on rp.id = bwt.template_pic_id left join res_model rm on rm.id = bwt.template_file_id where 1 = 1
and bwt.is_deleted = 0
order by id desc limit #{start},#{limit}
