select gpd.group_id as groupId, gpd.product_id as productId, gp.group_code as groupCode, gp.group_name as groupName, sd.name as compositeTypeName from group_product_details gpd left join group_product gp on gp.id = gpd.group_id left join sys_dictionary sd on sd.type = "restaurantAlbuginea" and value = gp.composite_type where gpd.group_id in
#{groupIdListItem}
and gpd.is_main = 1;
