select pc.id,pc.pid,pc.name,pc.is_leaf from pro_category pc
where 1=1
and pc.pid = #{pid,jdbcType=INTEGER}

 		select  from ((select * from pro_category  where name !='其他'   )    union    (select * from pro_category  where name  ='其他'  )) as pc
 		where 1=1

		 and pc.name_first_letter  = #{nameFirstLetter,jdbcType=VARCHAR} 
		  and pc.sys_code = #{sysCode,jdbcType=VARCHAR} 
		  and pc.creator = #{creator,jdbcType=VARCHAR} 
		  and pc.gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
		  and pc.modifier = #{modifier,jdbcType=VARCHAR} 
		  and pc.gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
		  and pc.is_deleted = #{isDeleted,jdbcType=INTEGER} 

			and pc.code  like CONCAT(CONCAT('%',#{code,jdbcType=VARCHAR}),'%')


			and pc.long_code  like CONCAT(CONCAT('%',#{longCode,jdbcType=VARCHAR}),'%')

		  and pc.pid = #{pid,jdbcType=INTEGER} 
		  and pc.name = #{name,jdbcType=VARCHAR} 
		  and pc.is_leaf = #{isLeaf,jdbcType=INTEGER} 
		  and pc.level = #{level,jdbcType=INTEGER} 
		  and pc.ordering = #{ordering,jdbcType=INTEGER} 
		  and pc.state = #{state,jdbcType=VARCHAR} 
		  and pc.att2 = #{att2,jdbcType=VARCHAR} 
		  and pc.att3 = #{att3,jdbcType=VARCHAR} 
		  and pc.att4 = #{att4,jdbcType=VARCHAR} 
		  and pc.att5 = #{att5,jdbcType=VARCHAR} 
		  and pc.att6 = #{att6,jdbcType=VARCHAR} 
		  and pc.date_att1 = #{dateAtt1,jdbcType=TIMESTAMP} 
		  and pc.date_att2 = #{dateAtt2,jdbcType=TIMESTAMP} 
		  and pc.num_att1 = #{numAtt1,jdbcType=INTEGER} 
		  and pc.num_att2 = #{numAtt2,jdbcType=INTEGER} 
		  and pc.num_att3 = #{numAtt3,jdbcType=DOUBLE} 
		  and pc.num_att4 = #{numAtt4,jdbcType=DOUBLE} 
		  and pc.remark = #{remark,jdbcType=VARCHAR} 
 		order by pc.ordering asc
