select
pa.*,pp.prop_value prop_value
from product_attribute pa
left join product_props pp on pp.id = pa.attribute_value_id
where 1=1
  and pp.prop_value = #{propValue,jdbcType=INTEGER} 
  and pa.attribute_type_value = #{attributeTypeValue,jdbcType=VARCHAR} 
  and pa.attribute_id = #{attributeId,jdbcType=INTEGER} 
  and pa.attribute_key = #{attributeKey,jdbcType=VARCHAR} 
  and pa.attribute_name = #{attributeName,jdbcType=VARCHAR} 
  and pa.attribute_value_id = #{attributeValueId,jdbcType=INTEGER} 
  and pa.attribute_value_key = #{attributeValueKey,jdbcType=VARCHAR} 
  and pa.attribute_value_name = #{attributeValueName,jdbcType=VARCHAR} 
  and pa.attribute_value_pic_ids = #{attributeValuePicIds,jdbcType=VARCHAR} 
  and pa.product_id = #{productId,jdbcType=INTEGER} 
  and pa.sys_code = #{sysCode,jdbcType=VARCHAR} 
  and pa.creator = #{creator,jdbcType=VARCHAR} 
  and pa.gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
  and pa.modifier = #{modifier,jdbcType=VARCHAR} 
  and pa.gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
  and pa.is_deleted = #{isDeleted,jdbcType=INTEGER} 
  and pa.product_code = #{productCode,jdbcType=VARCHAR} 
  and pa.att2 = #{att2,jdbcType=VARCHAR} 
  and pa.numa1 = #{numa1,jdbcType=INTEGER} 
  and pa.numa2 = #{numa2,jdbcType=INTEGER} 
  and pa.remark = #{remark,jdbcType=VARCHAR} 

	order by ${order}

		${orderNum}



	order by ${orders}

select id AS product_id from (

	select pa.product_id AS id from product_attribute pa
	LEFT JOIN product_props pp ON pp.id = pa.attribute_value_id
	LEFT JOIN base_product bp on bp.id = pa.product_id
	WHERE 1=1 and bp.is_deleted = 0
	and ${item}

)products group by products.id having count(products.id) = #{conditionCount,jdbcType=INTEGER}

		select
		pa.id,pa.attribute_type_value,pa.attribute_id,pa.attribute_key,pa.attribute_name,pa.attribute_value_id,
		pa.attribute_value_key,pa.attribute_value_name,pa.attribute_value_pic_ids,pa.product_id,pa.sys_code,
		pa.creator,pa.gmt_create,pa.modifier,pa.gmt_modified,pa.is_deleted,pa.product_code,
		pp.prop_value as pp_prop_value,
		pp2.filter_order as pp2_filter_order
		from product_attribute pa
		left join product_props pp on pp.id = pa.attribute_value_id
		left join product_props pp2 on pp2.id = pp.pid
        where 1=1
		  and pa.attribute_type_value = #{attributeTypeValue,jdbcType=VARCHAR} 
		  and pa.attribute_id = #{attributeId,jdbcType=INTEGER} 
		  and pa.attribute_key = #{attributeKey,jdbcType=VARCHAR} 
		  and pa.attribute_name = #{attributeName,jdbcType=VARCHAR} 
		  and pa.attribute_value_id = #{attributeValueId,jdbcType=INTEGER} 
		  and pa.attribute_value_key = #{attributeValueKey,jdbcType=VARCHAR} 
		  and pa.attribute_value_name = #{attributeValueName,jdbcType=VARCHAR} 
		  and pa.attribute_value_pic_ids = #{attributeValuePicIds,jdbcType=VARCHAR} 
		  and pa.product_id = #{productId,jdbcType=INTEGER} 
		  and pa.sys_code = #{sysCode,jdbcType=VARCHAR} 
		  and pa.creator = #{creator,jdbcType=VARCHAR} 
		  and pa.gmt_create = #{gmtCreate,jdbcType=TIMESTAMP} 
		  and pa.modifier = #{modifier,jdbcType=VARCHAR} 
		  and pa.gmt_modified = #{gmtModified,jdbcType=TIMESTAMP} 
		  and pa.is_deleted = #{isDeleted,jdbcType=INTEGER} 
		  and pa.product_code = #{productCode,jdbcType=VARCHAR} 
		  and pa.att2 = #{att2,jdbcType=VARCHAR} 
		  and pa.numa1 = #{numa1,jdbcType=INTEGER} 
		  and pa.numa2 = #{numa2,jdbcType=INTEGER} 
		  and pa.remark = #{remark,jdbcType=VARCHAR} 

			order by ${order}

				${orderNum}



		   order by ${orders}
