SELECT  count(s.id)  FROM base_series s
LEFT JOIN res_pic rp ON rp.id = s.`pic_id` AND rp.`is_deleted` = 0
WHERE  s.`display_status` = 1 AND s.`is_deleted` = 0

	AND pid IN

		#{item}



	AND  level = #{level,jdbcType=INTEGER}

SELECT s.id,s.name,b.brand_name,rp.pic_path,rp.`small_pic_info`,s.remark FROM base_series s LEFT JOIN res_pic rp ON rp.id = s.`pic_id` AND rp.`is_deleted` = 0 LEFT JOIN base_series s1 ON s1.id = s.pid LEFT JOIN base_brand b ON b.`brand_code` = s1.code WHERE s.`display_status` = 1 AND s.`is_deleted` = 0
AND s.pid IN
#{item}
AND s.level = #{level,jdbcType=INTEGER}
