mapper.select:
select

from
    wx_user_invite_record


        and id = #{record.id,jdbcType=BIGINT}


        and activity_id = #{record.activityId,jdbcType=BIGINT}


        and open_id = #{record.openId,jdbcType=VARCHAR}


        and nick_name = #{record.nickName,jdbcType=VARCHAR}


        and head_pic = #{record.headPic,jdbcType=VARCHAR}


        and user_id = #{record.userId,jdbcType=BIGINT}


        and invite_user_id = #{record.inviteUserId,jdbcType=BIGINT}


        and status = #{record.status,jdbcType=TINYINT}


        and card_flag = #{record.cardFlag,jdbcType=TINYINT}


        and card_Id = #{record.cardId,jdbcType=BIGINT}


        and remark = #{record.remark,jdbcType=VARCHAR}


        and creator = #{record.creator,jdbcType=VARCHAR}


        and gmt_create = #{record.gmtCreate,jdbcType=TIMESTAMP}


        and modifier = #{record.modifier,jdbcType=VARCHAR}


        and gmt_modified = #{record.gmtModified,jdbcType=TIMESTAMP}


        and is_deleted = #{record.isDeleted,jdbcType=INTEGER}



    limit #{start}, #{limit}

select
    count(1)
from
    wx_user_invite_record


      and id = #{id,jdbcType=BIGINT}


      and activity_id = #{activityId,jdbcType=BIGINT}


      and open_id = #{openId,jdbcType=VARCHAR}


      and nick_name = #{nickName,jdbcType=VARCHAR}


      and head_pic = #{headPic,jdbcType=VARCHAR}


      and user_id = #{userId,jdbcType=BIGINT}


      and invite_user_id = #{inviteUserId,jdbcType=BIGINT}


      and status = #{status,jdbcType=TINYINT}


      and card_flag = #{cardFlag,jdbcType=TINYINT}


      and card_Id = #{cardId,jdbcType=BIGINT}


      and remark = #{remark,jdbcType=VARCHAR}


      and creator = #{creator,jdbcType=VARCHAR}


      and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


      and modifier = #{modifier,jdbcType=VARCHAR}


      and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


      and is_deleted = #{isDeleted,jdbcType=INTEGER}
