SELECT

    ,

FROM
    interactive_zone_msg


        and id = #{interactiveZoneMsg.id,jdbcType=BIGINT}


        and topic_id = #{interactiveZoneMsg.topicId,jdbcType=BIGINT}


        and reply_id = #{interactiveZoneMsg.replyId,jdbcType=BIGINT}


        and pic_id = #{interactiveZoneMsg.picId,jdbcType=INTEGER}


        and operate_type = #{interactiveZoneMsg.operateType,jdbcType=INTEGER}


        and block_type = #{interactiveZoneMsg.blockType,jdbcType=INTEGER}


        and business_type = #{interactiveZoneMsg.businessType,jdbcType=INTEGER}


        and is_read = #{interactiveZoneMsg.isRead,jdbcType=INTEGER}


        and publish_user_id = #{interactiveZoneMsg.publishUserId,jdbcType=VARCHAR}


        and creator = #{interactiveZoneMsg.creator,jdbcType=VARCHAR}


        and gmt_create = #{interactiveZoneMsg.gmtCreate,jdbcType=TIMESTAMP}


        and modifier = #{interactiveZoneMsg.modifier,jdbcType=VARCHAR}


        and gmt_modified = #{interactiveZoneMsg.gmtModified,jdbcType=TIMESTAMP}


        and is_deleted = #{interactiveZoneMsg.isDeleted,jdbcType=INTEGER}


        and content = #{interactiveZoneMsg.content,jdbcType=LONGVARCHAR}



    limit #{start}, #{limit}

SELECT
