SELECT gpa.id, gpa.company_id, gpa.activity_name, gpa.activity_start_time, gpa.activity_end_time, gpa.group_valid_day, gpa.group_valid_hour, gpa.group_valid_minute, gpa.total_number, gpa.purchase_limit_amount, gpa.gather_flag, gpa.virtual_flag, gpa.coupon_flag, gpa.activity_status,
gpg.spu_id, gpg.sku_id, gpg.activity_price, gpg.sell_qty,
bgs.spu_name, bgs.product_name,
rp.pic_path from group_purchase_activity gpa INNER JOIN group_purchase_goods gpg on gpa.id = gpg.purchase_activity_id LEFT JOIN base_goods_spu bgs on gpg.spu_id = bgs.id LEFT JOIN res_pic rp on bgs.pic_id = rp.id AND rp.is_deleted = 0 where gpa.is_deleted = 0 AND gpa.company_id=#{companyId,jdbcType = INTEGER}
AND gpa.activity_status = #{status,jdbcType=INTEGER}
AND gpa.activity_status in
#{item,jdbcType=INTEGER}
ORDER BY gpa.gmt_create DESC
