mapper.select:
select

from design_plan_render_scene
where id = #{id,jdbcType=BIGINT}

SELECT fhdpd.full_house_plan_id AS fullHousePlanId, fhdp.plan_name AS fullHousePlanName, fhdpd.space_type AS spaceType, t.name AS spaceName, fhdpd.render_scene_plan_id AS designPlanRenderSceneId, fhdpd.plan_render_resource_id AS planRenderResourceId, d.pic_path AS planResourcPicPath, sc.space_areas AS spaceAreas, dprs.plan_name AS planName, d.id AS resRenderPicId, fhdpd.gmt_create AS gmtCreateTime, fhdpd.gmt_modified AS gmtModifiedTime, IFNULL(bps.name,'') AS designPlanStyleName, dprs.design_template_id AS designTemplateId, IFNULL(dpsr.house_id,0) AS houseId, IFNULL(bh.living_id,0) AS livingId, dprs.design_style_id AS designPlanStyleId FROM full_house_design_plan_detail fhdpd LEFT JOIN full_house_design_plan fhdp on fhdp.id = fhdpd.full_house_plan_id left join design_plan_store_release dpsr on dpsr.uuid = fhdp.vr_resource_uuid LEFT JOIN base_house bh on bh.id = dpsr.house_id LEFT JOIN (SELECT value,name,ordering from sys_dictionary where type = 'houseType') t ON t.value = fhdpd.space_type LEFT JOIN (SELECT pic_path,id,design_scene_id from res_render_pic where file_key = 'design.designPlan.render.small.pic' and is_deleted = 0) d ON fhdpd.render_scene_plan_id = d.design_scene_id LEFT JOIN (SELECT design_template_id,id,plan_name,design_style_id from design_plan_render_scene WHERE is_deleted = 0) dprs ON dprs.id = fhdpd.render_scene_plan_id LEFT JOIN design_templet AS dt ON dprs.design_template_id = dt.id LEFT JOIN (SELECT id,space_function_id,space_areas,space_shape from space_common where is_deleted = 0) AS sc ON sc.id = dt.space_common_id left join base_product_style bps ON bps.id = dprs.design_style_id WHERE fhdpd.full_house_plan_id = #{fullHousePlanId} AND (fhdp.source_type = 2 or fhdp.source_type = 5) AND fhdp.is_deleted = 0
AND dt.id = #{designTemplateId}
GROUP BY fhdpd.id order by t.ordering asc,fhdpd.gmt_modified desc;

select business_id from res_render_pic where rendering_type = 4 and is_deleted = 0 and file_key ='design.designPlan.render.pic' AND business_id in
#{item.designPlanId}
GROUP BY business_id

SELECT dprs.id AS designRenderSceneId, dprs.plan_name AS fullHousePlanName, IFNULL(arts.house_id,0) AS houseId, dprs.user_id AS userId, dprs.design_style_id AS designPlanStyleId, sc.space_function_id AS spaceType, sc.space_name AS spaceName, d.pic_path AS planResourcPicPath, sc.space_areas AS spaceAreas, bps.name AS designPlanStyleName FROM design_plan_render_scene dprs left join auto_render_task_state arts on arts.business_id = dprs.id LEFT JOIN design_templet AS dt ON dprs.design_template_id = dt.id LEFT JOIN (SELECT id,space_function_id,space_areas,space_shape,space_name from space_common where is_deleted = 0) AS sc ON sc.id = dt.space_common_id LEFT JOIN (SELECT pic_path,id,design_scene_id from res_render_pic where file_key = 'design.designPlan.render.small.pic' and is_deleted = 0) d ON dprs.id = d.design_scene_id left join base_product_style bps ON bps.id = dprs.design_style_id WHERE dprs.id in
#{item}
AND dprs.is_deleted = 0 GROUP BY dprs.id

