mapper.select:
SELECT u.commision as commisionCount, su.id as userId, su.user_name as nickName, rp.pic_path as userHeadPicPath, sd.name as userTypeName, CONVERT(IF(ISNULL(i.inviteIdCount ),0,i.inviteIdCount ),SIGNED) AS userInviteCount, su.sex as sex, sd2.name as sourceCompanyName FROM sys_user as su left join sys_dictionary sd on sd.type='userType' and sd.value = su.user_type and sd.is_deleted = 0 left join sys_dictionary sd2 on sd2.type='userSourceCompany' and sd2.value = su.source_company and sd2.is_deleted = 0 left join res_pic rp on rp.id = su.pic_id and rp.is_deleted = 0 LEFT JOIN (SELECT count(ui.id) as inviteIdCount, ui.invite_id ,ui.is_deleted from user_invite ui where ui.is_deleted = 0
AND date_format(ui.register_time,'%Y') = #{year}
AND date_format(ui.register_time,'%m') = #{month}
GROUP BY ui.invite_id) i on i.invite_id = su.id LEFT JOIN ( SELECT IFNULL(sum(i.total_commision)/100,0) as commision, i.agent_id, i.is_deleted FROM intermediary_commision i left join sys_user s on s.id = i.agent_id WHERE i.collect_status = 1 AND i.is_deleted = 0 and i.is_deleted = 0
AND date_format(i.gmt_create,'%Y') = #{year}
AND date_format(i.gmt_create,'%m') = #{month}
GROUP BY i.agent_id ORDER BY sum(i.total_commision) DESC LIMIT #{start}, #{limit} ) as u ON u.agent_id = su.id WHERE u.is_deleted = 0 and u.is_deleted =0

SELECT IFNULL(sum(total_commision)/100,0) FROM intermediary_commision WHERE collect_status = 1 AND commision_status = 1 AND is_deleted = 0 AND agent_id = #{userId}
AND date_format(commision_remit_time, '%Y') = #{year}
AND date_format(commision_remit_time,'%m') = #{month}

SELECT i.pay_number AS payNumber, u.decoration_company_name AS uitCompanyName, i.commision_status AS status, i.total_commision/100 AS commision, i.commision_in_time AS commisionIntime, i.commision_remit_time AS commisionRemitTime, s.nick_name AS userName, IF(i.commision_status!=1,'未返现','已返现') AS statusName FROM intermediary_commision i LEFT JOIN sys_user s ON s.id = i.owner_id AND s.is_deleted = 0 LEFT JOIN user_invite_track u on u.id = i.track_id WHERE i.collect_status = 1 and u.is_deleted = 0 AND i.total_commision is not null AND i.total_commision !=0 AND i.is_deleted = 0 AND i.agent_id = #{userId} AND date_format(i.gmt_modified, '%Y') = #{year}
AND date_format(i.gmt_modified,'%m') = #{month}
ORDER BY i.gmt_modified DESC LIMIT #{start}, #{limit}
