mapper.select:
select
*
from wx_act_lucky_wheel
where is_deleted=0

	and id = #{id,jdbcType=VARCHAR}


	and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


	and begin_time = #{beginTime,jdbcType=TIMESTAMP}


	and end_time = #{endTime,jdbcType=TIMESTAMP}


	and config_item = #{configItem,jdbcType=INTEGER}


	and lottery_num_per_day_defalut = #{lotteryNumPerDayDefalut,jdbcType=INTEGER}


	and lottery_num_per_day_max = #{lotteryNumPerDayMax,jdbcType=INTEGER}


	and lottery_num_defalut = #{lotteryNumDefalut,jdbcType=INTEGER}


	and lottery_num_max = #{lotteryNumMax,jdbcType=INTEGER}


	and lottery_total_count = #{lotteryTotalCount,jdbcType=INTEGER}


	and reg_count = #{regCount,jdbcType=INTEGER}


	and awards_total_count = #{awardsTotalCount,jdbcType=INTEGER}


	and is_enable = #{isEnable,jdbcType=INTEGER}


	and user_id = #{userId,jdbcType=BIGINT}


	and app_id = #{appId,jdbcType=VARCHAR}


	and app_name = #{appName,jdbcType=VARCHAR}


	and company_id = #{companyId,jdbcType=BIGINT}


	and company_name = #{companyName,jdbcType=VARCHAR}


	and creator = #{creator,jdbcType=VARCHAR}


	and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


	and modifier = #{modifier,jdbcType=VARCHAR}


	and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


	and rule = #{rule,jdbcType=LONGVARCHAR}



	and now() < begin_time and is_enable=1


	and begin_time < now() and now() < end_time and is_enable=1


	and  (end_time < now() or is_enable=0)

order by gmt_create desc
