mapper.select:
		select
		*
		from wx_act_lucky_wheel_prize
		where is_deleted=0

			and id = #{id,jdbcType=VARCHAR}


        and act_id = #{actId,jdbcType=VARCHAR}


        and name = #{name,jdbcType=VARCHAR}


        and img = #{img,jdbcType=VARCHAR}


        and num = #{num,jdbcType=INTEGER}


        and remain_num = #{remainNum,jdbcType=INTEGER}


        and type = #{type,jdbcType=INTEGER}


        and value = #{value,jdbcType=DOUBLE}


       and probability = #{probability}


        and order_seq = #{orderSeq,jdbcType=INTEGER}


        and num_per_day = #{numPerDay,jdbcType=INTEGER}


       and today_remain_num = #{todayRemainNum,jdbcType=INTEGER}


        and app_id = #{appId,jdbcType=VARCHAR}


        and company_id = #{companyId,jdbcType=BIGINT}


       and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}

      order by order_seq
