mapper.select:
select
*
from wx_act_red_packet_config
where is_deleted=0

	and id = #{id,jdbcType=VARCHAR}


	and act_id = #{actId,jdbcType=VARCHAR}


	and red_packet_seq = #{redPacketSeq,jdbcType=INTEGER}


	and min_amount = #{minAmount}


	and max_amount = #{maxAmount}


	and request_invite_num = #{requestInviteNum,jdbcType=INTEGER}


	and desc = #{desc,jdbcType=VARCHAR}


	and app_id = #{appId,jdbcType=VARCHAR}


	and company_id = #{companyId,jdbcType=BIGINT}


	and creator = #{creator,jdbcType=VARCHAR}


	and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


	and modifier = #{modifier,jdbcType=VARCHAR}


	and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}

order by red_packet_seq
