mapper.insert:
insert into user_node_info_rel (id, uuid, user_id, 
  node_id, detail_type, status, 
  creator, gmt_create, modifier, 
  gmt_modified, is_deleted, remark
  )
values (#{id,jdbcType=INTEGER}, #{uuid,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, 
  #{nodeId,jdbcType=INTEGER}, #{detailType,jdbcType=TINYINT}, #{status,jdbcType=TINYINT}, 
  #{creator,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}, 
  #{gmtModified,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=TINYINT}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select
from user_node_info_rel where is_deleted = 0
limit #{start},#{size}

select

from
    user_node_info_rel


        and uuid = #{uuid,jdbcType=VARCHAR}


        and user_id = #{userId,jdbcType=INTEGER}


        and node_id = #{nodeId,jdbcType=INTEGER}


        and detail_type = #{detailType,jdbcType=TINYINT}


        and status = #{status,jdbcType=TINYINT}


        and creator = #{creator,jdbcType=VARCHAR}


        and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


        and modifier = #{modifier,jdbcType=VARCHAR}


        and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        and is_deleted = #{isDeleted,jdbcType=TINYINT}


        and remark = #{remark,jdbcType=VARCHAR}
