mapper.select:
select t1.id, t1.business_id, t1.pay_model_config_id, t1.effective_time, t1.expiry_time, t1.is_deleted, t1.creator, t1.gmt_create, t1.modifier, t1.gmt_modified from pay_model_group_ref AS t1 LEFT OUTER JOIN pay_model_config AS t2 ON t1.pay_model_config_id = t2.id where t1.is_deleted = '0' AND t2.is_deleted = '0' AND t1.expiry_time > NOW()
AND t2.time_type = #{timeType}
AND t2.range_type = #{rangeType}
AND t1.business_id = #{businessId,jdbcType=INTEGER}
AND t1.pay_model_config_id IN
'${c.id}'
ORDER BY t1.expiry_time DESC

select

from pay_model_group_ref
where is_deleted = '0'

    AND business_id = #{businessId,jdbcType=INTEGER}


    AND pay_model_config_id = #{payModelConfigId,jdbcType=INTEGER}

SELECT t2.package_name AS packageName,t2.time_type AS timeType,t1.expiry_time AS expiryTime,t2.biz_type AS bizType FROM pay_model_group_ref AS t1 LEFT OUTER JOIN pay_model_config AS t2 ON t2.id = t1.pay_model_config_id WHERE 1 = 1 AND t1.is_deleted = '0' AND t2.is_deleted = '0' AND t1.expiry_time >= NOW()
AND t1.business_id = #{businessId}
AND t2.range_type = #{rangeType}
AND (t2.biz_type = #{bizType,jdbcType=VARCHAR} OR t2.biz_type = 'renderTypeGive')
ORDER BY t1.expiry_time DESC

SELECT t2.pay_model_config_id AS payModelConfigId, t2.id AS payModelGroupRefId, t1.package_user_type AS packageUserType, t2.expiry_time AS expiryTime FROM pay_model_config AS t1 LEFT OUTER JOIN pay_model_group_ref AS t2 ON t1.id = t2.pay_model_config_id WHERE 1 = 1 AND t1.is_deleted = '0' AND t2.is_deleted = '0' AND t1.biz_type = 'companyFreeRenderPlan' AND t1.business_type = '1' AND (NOW() BETWEEN t2.effective_time AND t2.expiry_time) AND t1.range_type = '2' AND (t1.package_user_type = #{packageUserType} OR t1.package_user_type = #{userType})
AND t1.platform_id = #{platformId}
AND t2.business_id = #{companyId} ORDER BY t2.expiry_time DESC LIMIT 1

SELECT t2.package_name AS packageName, t2.time_type AS timeType, t1.expiry_time AS expiryTime, t2.biz_type AS bizType FROM pay_model_group_ref AS t1 LEFT OUTER JOIN pay_model_config AS t2 ON t2.id = t1.pay_model_config_id WHERE 1 = 1 AND t1.is_deleted = '0' AND t2.is_deleted = '0' AND t1.expiry_time >= NOW()
AND t1.business_id = #{businessId}
AND t2.biz_type = #{bizType}
ORDER BY t1.expiry_time DESC
